import React from 'react';
// import PropTypes from 'prop-types';
import Form from './components/Form';
import Card from './components/Card';
import { object } from 'prop-types';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: 0,
      cardAttr2: 0,
      cardAttr3: 0,
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      isSaveButtonDisabled: true,
      cardDate: [],
    };
  }

  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({ [name]: value }, () => {
      const {
        cardName,
        cardDescription,
        cardImage,
        cardRare,
        cardAttr1,
        cardAttr2,
        cardAttr3,
      } = this.state;
      const arrayStrings = [cardName, cardDescription, cardImage, cardRare];
      const arrayLength0 = arrayStrings.filter((state) => state.length < 1);
      const limitAll = 210;
      const limit = 90;
      const isArrayBiggerThan1 = arrayLength0.length > 0;
      const cardeAttr1 = parseInt(cardAttr1, 10);
      const cardeAttr2 = parseInt(cardAttr2, 10);
      const cardeAttr3 = parseInt(cardAttr3, 10);
      const isBiggerThan210 = (cardeAttr1 + cardeAttr2 + cardeAttr3) > limitAll;
      const attr1 = cardAttr1 < 0 || cardAttr1 > limit;
      const attr2 = cardAttr2 < 0 || cardAttr2 > limit;
      const attr3 = cardAttr3 < 0 || cardAttr3 > limit;
      const allAttr = attr1 || attr2 || attr3;
      const isButtonDisable = isArrayBiggerThan1 || allAttr || isBiggerThan210;

      this.setState({
        isSaveButtonDisabled: isButtonDisable,
      });
    });
  }

  onSaveButtonClick = () => {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    } = this.state;
    const objCard = {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    };
    this.setState((prevState) => {
      this.setState({
        cardDate: [objCard],
      });
    });
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardTrunfo,
      cardImage,
      cardRare,
      isSaveButtonDisabled,
    } = this.state;
    return (
      <main>
        <h1>Tryunfo</h1>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardTrunfo={ cardTrunfo }
          onInputChange={ this.onInputChange }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onSaveButtonClick={ this.onSaveButtonClick }
        />
        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardTrunfo={ cardTrunfo }
          cardImage={ cardImage }
          cardRare={ cardRare }
        />
      </main>
    );
  }
}

export default App;

