import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import CardList from './components/CardList';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: 0,
      cardAttr2: 0,
      cardAttr3: 0,
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      isSaveButtonDisabled: true,
      cardDate: [],
      hasACardTrunfo: false,
    };
  }

  verifyButtonDisable = () => {
    const {
      cardName,
      cardDescription,
      cardImage,
      cardAttr1,
      cardAttr2,
      cardAttr3,
    } = this.state;
    const v = cardName.length < 1 || cardDescription.length < 1 || cardImage.length < 1;
    const limitAll = 210;
    const limit = 90;
    const iB210 = Number(cardAttr1) + Number(cardAttr2) + Number(cardAttr3) > limitAll;
    const attr1 = cardAttr1 < 0 || cardAttr1 > limit;
    const attr2 = cardAttr2 < 0 || cardAttr2 > limit;
    const attr3 = cardAttr3 < 0 || cardAttr3 > limit;
    const allAttr = attr1 || attr2 || attr3;
    const isButtonDisable = v || allAttr || iB210;

    this.setState({
      isSaveButtonDisabled: isButtonDisable,
    });
  }

  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({ [name]: value }, () => {
      this.verifyButtonDisable();
    });
  }

  onSaveButtonClick = (event) => {
    event.preventDefault();
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      isSaveButtonDisabled,
    } = this.state;
    const objCard = {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      isSaveButtonDisabled,
    };
    this.setState((prevState) => {
      this.hasTrunfo();
      this.setState({
        cardDate: [...prevState.cardDate, objCard],
        cardName: '',
        cardDescription: '',
        cardAttr1: 0,
        cardAttr2: 0,
        cardAttr3: 0,
        cardImage: '',
        cardRare: 'normal',
        cardTrunfo: false,
        isSaveButtonDisabled: true,
      });
    });
  }

  hasTrunfo = () => {
    const { cardDate, hasACardTrunfo } = this.state;
    const listOfTrunfo = cardDate.filter((card) => card.cardTrunfo === true);
    const hasTrunfo = listOfTrunfo.length > 0;
    this.setState({
      hasACardTrunfo: hasTrunfo,
    });
    console.log(hasTrunfo);
    return hasACardTrunfo;
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardTrunfo,
      cardImage,
      cardRare,
      isSaveButtonDisabled,
      cardDate,
    } = this.state;
    return (
      <main>
        <h1>Tryunfo</h1>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardTrunfo={ cardTrunfo }
          cardImage={ cardImage }
          cardRare={ cardRare }
          onInputChange={ this.onInputChange }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onSaveButtonClick={ this.onSaveButtonClick }
          hasTrunfo={ this.hasTrunfo }
        />
        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardTrunfo={ cardTrunfo }
          cardImage={ cardImage }
          cardRare={ cardRare }
        />
        { cardDate.length > 0 ? <CardList date={ cardDate } /> : '' }
      </main>
    );
  }
}

export default App;
